            var jm = parseDomForHtml(list[j], 'a&&Text').replace(/第|集|话|期/g, '').replace(/预告/g, '📢');
            if (list.length < 5) {
                var col = 'text_3'
            } else {
                var col = jm.length > 5 ? 'text_3' : 'text_5'
            }
            d.push({
                title: jm,
                url: parseDom(list[j], 'a&&href') + lazy,
                extra: {
                    blockRules: ['m3u8', 'mp4', '.m4a', '.mp3', '.flv', '.avi', '.3gp', '.mpeg', '.wmv', '.mov', '.rmvb', '.gif', '.jpg', '.jpeg', '.png', '.ico', '.svg']
                },
                col_type: col
            });
        }
        // 开始选集分区
