        canBack: true,
        js: $.toString((rule) => {
            var token_timer = function() {
                var timer = setInterval(() => {
                    try {
                        var urls = _getUrls();
                        var exclude = /playm3u8|m3u8\.tv|min\.css|404\.m3u8|\.html|\.php/;
                        var contain = /\.mp4|\.m3u8|\.flv|\.avi|\.mpeg|\.wmv|\.mov|\.rmvb|\.dat|qqBFdownload|mime=video%2F|video_mp4/;
                        for (var i in urls) {
                            //fy_bridge_app.log(urls[i]);
                            if (!exclude.test(urls[i]) && contain.test(urls[i])) {
                                clearInterval(timer);
                                fy_bridge_app.playVideo(urls[i]);
                                //静音
                                function muteMe(elem) {
                                    try {
                                        elem.pause();
                                    } catch (e) {}
                                }
                                var videos = document.querySelectorAll("video"),
                                    audios = document.querySelectorAll("audio"),
                                    v2 = document.querySelectorAll("embed"),
                                    v3 = document.querySelectorAll("#player");
                                try {
                                    [].forEach.call(videos, function(video) {
                                        muteMe(video);
                                    });
                                } catch (e) {}
                                try {
                                    [].forEach.call(audios, function(audio) {
                                        muteMe(audio);
                                    });
                                } catch (e) {}
                                try {
                                    [].forEach.call(v2, function(v) {
                                        muteMe(v);
                                    });
                                } catch (e) {}
                                try {
                                    [].forEach.call(v3, function(v) {
                                        muteMe(v);
                                    });
                                } catch (e) {}
                                return;
                                //return urls[i];
                            }
                        }
                    } catch (e) {}
                }, 200)
            };
            token_timer();
        }, MY_RULE.title)
    }
})
setResult(d)

