js:
var d = [];
var html = getResCode();



//选择器填写区

//信息接口，列表可以为空，不为空则信息内容基于列表，且列表不是数组

var 列表 = "body&&div.albumDetailWrap";
      var 信息 = "h1&&Text";
      var 信息1 = "ul&&li,0&&Text";
      var 信息2 = "ul&&li,1&&Text";
      var 信息3 = "ul&&li,2&&Text";
      var 信息4 = "ul&&li,3&&Text";
      var 封面 = "div.albumDetailImg&&img&&src";


//简介接口
var 简介 = "p.albumDetailIntroTxt&&data-content";


//路线名接口，路线列表与路线列表正则只能填一个，且返回数组

var 路线列表 = "body&&section.mod.aui-margin-t-15";
var 路线列表正则 = "";
      var 路线名 = "span.mod-head-name&&Text";


//集数接口
//只要是列表，都要返回数组

var 集数总列表 = "body&&div.albumSelect.albumSelectHidden";
      var 集数列表 = "ul&&li";
            var 集数名 = "a&&Text";
            var 集数地址 = "a&&href";


//接口解析（非必填），填写了则默认免探嗅解析
//默认未填格式：var 免嗅解析 = `@lazyRule=.js:`;

var 免嗅解析 = `@lazyRule=.js:var url = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1]);decodeURIComponent(base64Decode(url.url))`;







//以下代码，如非必要请勿修改

//探嗅代码
var _x5 = $.toString(() => {
    var urls = _getUrls();
    for (var i in urls) {
     if (urls[i].indexOf("url=") == -1){
        if (urls[i].match(/\.mp3|\.mp4|\.m3u8/)) {
            fy_bridge_app.log(urls[i]);
            return urls[i]
        }
       }
    }
});


//信息接口代码
if (列表 != "") {
   if(列表.indexOf("&&Html") == -1){
     列表 = 列表 + "&&Html";
    } 
   var code = parseDomForHtml(html, 列表);
} else {
    var code = html;
}

信息 = parseDomForHtml(code, 信息);
信息1 = parseDomForHtml(code, 信息1);
信息2 = parseDomForHtml(code, 信息2);
信息3 = parseDomForHtml(code, 信息3);
信息4 = parseDomForHtml(code, 信息4);
封面 = parseDom(code, 封面);

if (信息.length > 20) {
    信息 = 信息.replace(信息, 信息.substring(0, 20) + "...");
}
if (信息1.length > 17) {
    信息1 = 信息1.replace(信息1, 信息1.substring(0, 17) + "...");
}
if (信息3.length > 20) {
    信息3 = 信息3.replace(信息3, 信息3.substring(0, 20) + "...");
}

var 标题 = 信息 + "\n" + 信息1 + "\n" + 信息2;
var 副标题 = 信息3 + "\n" + 信息4;

d.push({
    title: 标题.replace(/  /g, " "),
    desc: 副标题,
    pic_url: 封面,
    url: MY_URL,
    col_type: 'movie_1_vertical_pic_blur'
});

//简介接口代码
简介 = parseDomForHtml(html, 简介).replace("　　", "").replace("简介：", "");

d.push({
    title: "　　" +简介 + "\n ",
    col_type: 'long_text'
});

